import React from 'react'
import { Header, Button,Grid,Form,Input,Segment,TextArea,Divider } from 'semantic-ui-react';
import DateTimePicker from 'react-datetime-picker';

const SendAppointment = () => {
    const [time, setTime] = useState(new Date());

    const publicVapidKey = 'BMffGk0gRxLPOSi-eOlXoR1ahY9Ce7uBY3010C06TeMoRYS_6n1A4ItVeOeNYutDlhPK27WW5UMrdyjBEj_-Pxo';

    async function send() {
        //register service worker
        const register = await navigator.serviceWorker.register('/sw.js', {
            scope: '/'
        });

        //register push
        console.log('Registering push...')
        const subscription = await register.pushManager.subscribe({
            userVisibleOnly: true,
            applicationServerKey: urlBase64ToUint8Array(publicVapidKey)
        });

        //Send push notification
        await fetch("http://localhost:5000/api/subscribe", {
            method: "POST",
            body: JSON.stringify(subscription),
            headers: {
                "content-type": "application/json"
            }
        });
    }

    function urlBase64ToUint8Array(base64String) {
        const padding = "=".repeat((4 - base64String.length % 4) % 4);
        const base64 = (base64String + padding)
            .replace(/\-/g, "+")
            .replace(/_/g, "/");

        const rawData = window.atob(base64);
        const outputArray = new Uint8Array(rawData.length);

        for (let i = 0; i < rawData.length; ++i) {
            outputArray[i] = rawData.charCodeAt(i);
        }
        return outputArray;
    }
    return (
        <div style={{ alignContent: "center", margin: "100px" }}>

            <Grid textAlign='center'>
                <Grid.Column width={5}>

                    <Segment stacked style={{ backgroundColor: "lightGray" }} textAlign='center'>
                        <Form size='large' >
                            <Header as='h2' color='teal' textAlign='center'>
                            SEEEEEEEEEEEEEEEEEEEEEEEEENNNNNNNNDDDDDDDDDDDD
                            </Header>
                            <Form.Field
                                required
                                id='form-input-control-last-name'
                                control={TextArea}
                                label='appointment text'
                                name='appointmentletter'
                                placeholder='feedback'
                            />
                            <DateTimePicker
        onChange={setTime}
        value={time}
      />
                            <Form.Field
                                required
                                id='form-input-control-last-name'
                                control={Input}
                                name='username'
                                type='text'
                                label='username'
                            />

                            <Divider hidden />
                            <Button color="twitter" >Send Appointment</Button>
                            <Divider hidden />

                        </Form>
                    </Segment>
                </Grid.Column>


            </Grid>
        </div>
    )
}

export default SendAppointment
