import React, { useState } from "react";
import storage from "../../firebase";
import axios from 'axios'
const FileUp = () => {

  const [video,setVideo] = useState(null)
  const [imageUrls, setImageUrls] = useState([]);
  const [videoUrls, setVideoUrls] = useState([]);
  const [files, setFiles] = useState([]);
  const [housename,setHousename] = useState('')
  const [description,setDescription] = useState('')
  const [numberofbeds,setNumberOfBeds] = useState(0)
  const [feepermonth,setFeeperMonth] = useState(0)
  const [size,setSize] = useState(0)
  const [available,setAvailable] = useState(false)
  const [propertytype,setPropertytype] = useState('') 
  const [housedata,setHouseData]= useState(null)


  const [progress, setProgress] = useState(0);

  const handleChange = (e) => {
    console.log(e.target.name)
    for (let i = 0; i < e.target.files.length; i++) {
      const newImage = e.target.files[i];
      newImage["id"] = Math.random();
      setFiles((prevState) => [...prevState, newImage]);
    }
  };
  const handleVideoChange=(e)=>{
      setVideo(e.target.files[0])

  }
  const handleNameChange=(e)=>{
    setHousename(e.target.value)
    console.log(housename)
  }
  const handleDescrptionChange=(e)=>{
    setDescription(e.target.value)
  }
  const handleTypeChange=(e)=>{
    setPropertytype(e.target.value)
  }
  const handleFeeChange=(e)=>{
    setFeeperMonth(e.target.value)
  }
  const handleSizeChange=(e)=>{
    setSize(e.target.value)
  }
  const handleAvailableChange=(e)=>{
    setAvailable(e.target.value)
  }
  const handleBedChange=(e)=>{
    setNumberOfBeds(e.target.value)
  }
  // const handleInputChange = (e) => {
  //     const value = e.target.value;
  //     setHousename({ ...housename, [e.target.name]: value });
  //     setDescription({ ...description, [e.target.name]: value });
  //     setPropertytype({ ...propertytype, [e.target.name]: value });
  //     setFeeperMonth({ ...feepermonth, [e.target.name]: value });
  //     setSize({ ...size, [e.target.name]: value });
  //     setAvailable({ ...available, [e.target.name]: value });
  //     setNumberOfBeds({ ...numberofbeds, [e.target.name]: value });

  //   };

  const createHouse = async (housedata) => {
    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.post("http://localhost:5000/api/multipleFiles", housedata,config); 
      console.log(res) 
    } catch (err) {
      console.log(err)
    }
  };
  const handleMultipleFileSubmit= async ()=>{
    const formData = new FormData();
      formData.append('housename', housename);
      formData.append('description', description);
      formData.append('numberofbeds', numberofbeds);
      formData.append('size', size);
      formData.append('feepermonth', feepermonth);
      formData.append('available', available);
      formData.append('propertytype', propertytype);
      formData.append('video', videoUrls);
      imageUrls.map((url)=>{
        formData.append('files', url);
      })

      await createHouse(formData);

  }

  const handleUpload = () => {
    const promises = [];
    const uploadTaskTwo = storage.ref(`images/${video.name}`).put(video)
    promises.push(uploadTaskTwo);
    uploadTaskTwo.on(
      "state_changed",
      (snapshot) => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      (error) => {
        console.log(error);
      },
      async () => {
       const urls = await storage.ref("images").child(video.name).getDownloadURL()
            setVideoUrls((prevState) => [...prevState, urls]);
            console.log("this is a video part",urls)

      }

      )

    files.map((image) => {
      const uploadTask = storage.ref(`images/${image.name}`).put(image);
      promises.push(uploadTask);
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          const progress = Math.round(
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100
          );
          setProgress(progress);
        },
        (error) => {
          console.log(error);
        },
        async () => {
          const urls = await storage.ref("images").child(image.name).getDownloadURL();
            setImageUrls((prevState) => [...prevState, urls]);
            console.log("this is a iamge part",urls)

        }
      );

    });



    Promise.all(promises)
      .then(
        alert("file uploaded successfully")
      )
      .catch((err) => console.log(err));

  };



  return (
    <div style={{marginTop:"100px"}}>
      <progress value={progress} max="100" />
      <br />
      <br />
      <input type="file" name="files" multiple onChange={handleChange} />
      <input type="file" name="video"  onChange={handleVideoChange} />
      <div className="addProductItem">
          <label>House Name</label>
          <input
            type="text"
            placeholder="housename"
            name="housename"
            onChange={handleNameChange}
          />
        </div>
        <div className="addProductItem">
          <label>Description</label>
          <input
            type="text"
            placeholder="description"
            name="description"
            onChange={handleDescrptionChange}
          />
        </div>
        <div className="addProductItem">
          <label>Fee per Month</label>
          <input
            type="number"
            placeholder="feepermonth"
            name="feepermonth"
            onChange={handleFeeChange}
          />
        </div>
        <div className="addProductItem">
          <label>Size</label>
          <input
            type="number"
            placeholder="size"
            name="size"
            onChange={handleSizeChange}
          />
        </div>
        <div className="addProductItem">
          <label>Number of Beds</label>
          <input
            type="number"
            placeholder="numberofbeds"
            name="numberofbeds"
            onChange={handleBedChange}
          />
        </div>
        <div className="addProductItem">
          <label>propertytype</label>
          <input
            type="text"
            placeholder="propertytype"
            name="propertytype"
            onChange={handleTypeChange}
          />
        </div>
        <div className="addProductItem">
          <label>Available?</label>
          <select name="available" id="available" onChange={handleAvailableChange}>
            <option value="false">No</option>
            <option value="true">Yes</option>
          </select>
        </div> 
      <button onClick={handleUpload}>create upload</button>
      <button onClick={handleMultipleFileSubmit}>final Upload</button>

      <br/>

      <br />
      {imageUrls.map((url, i) => (
        <div key={i}>
          <a href={url} target="_blank">
            {url}
          </a>
        </div>
      ))}
      <br />
      {imageUrls.map((url, i) => (
        <img
          key={i}
          style={{ width: "500px" }}
          src={url || "http://via.placeholder.com/300"}
          alt="firebase-image"
        />
      ))}
      {
        <video
          style={{ width: "500px" }}
          src={videoUrls || "http://via.placeholder.com/300"}
          alt="firebase-image"
          autoPlay
        />
      }
    </div>
  );
}

export default FileUp
