import axios from 'axios'
import React,{useState} from 'react'
import { Form,Button,Input,Header,Checkbox,Divider } from 'semantic-ui-react'
const ContractForm = () => {
    const [recipient, setRecipient] = useState('')
    const sendEmail = async()=>{
        const config = {
            header: {
              "Content-Type": "application/json",
            },
          };
          try {
            console.log(recipient)
            const res = await axios.post("http://localhost:5000/api/sendContractForm",recipient, config);
            console.log(res)
          } catch (err) {
            console.log(err)
          }
    }
    return (
        <div>
            <Form size='large' >
                      <Header as='h2' color='teal' textAlign='center'>
                        send a contract form
                      </Header>
                      <Divider hidden />

                      <Form.Field
                        required
                        id='form-input-control-last-name'
                        control={Input}
                        name='tenantEmail'
                        type='text'
                        placeholder='email of the tenant who requested the house'
                        label='Email'
                        onChange={e=>setRecipient(e.target.value)}
                      />
                      <Divider hidden />
                      <Button color="twitter" onClick={sendEmail} >Send Email</Button>

                    </Form>
        </div>
    )
}

export default ContractForm
