import React,{useState} from 'react'
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react'
import storage from "../../firebase";
import axios from 'axios'
const RegisterHouses = () => {
  const [housedata,setHouseData]= useState(null)
  const [housename,setHousename] = useState("")
  const [description,setDescription] = useState("")
  const [numberofbeds,setNumberOfBeds] = useState(0)
  const [feepermonth,setFeeperMonth] = useState(0)
  const [size,setSize] = useState(0)
  const [available,setAvailable] = useState(false)
  const [propertytype,setPropertytype] = useState("")
  const [files, setFiles] = useState([]);
  const [video, setVideo] = useState(null);
  const [urls,setUrls] = useState(null)
  const [uploaded, setUploaded] = useState(0);

  const handleChange = (e) => {
    const value = e.target.value;
    setHouseData({ ...housedata, [e.target.name]: value });
  };
  const handleChangeForImg= (e)=>{
    for (let i = 0; i < e.target.files.length; i++) {
      const newImage = e.target.files[i];
      newImage["id"] = Math.random();
      setFiles((prevState) => [...prevState, newImage]);
    }

  }
  const createHouse = async (housedata) => {
    try {
      await axios.post("/houses/multipleFiles", housedata);  
    } catch (err) {
      throw err
    }
  };

  const upload = (items) => {
    items.forEach((item) => {
    /*   const promises =[]
      item.files.map((image) => {
        const uploadTask = storage.ref(`images/${image.name}`).put(image);
        promises.push(uploadTask);
        uploadTask.on(
          "state_changed",
          (error) => {
            console.log(error);
          },
          async () => {
            await storage
              .ref("images")
              .child(image.name)
              .getDownloadURL()
              .then((urls) => {
                setUrls((prevState) => [...prevState, urls]);
              });
          }
        );
      }) */
      console.log(item.files)
      const fileName = new Date().getTime() + item.label + item.file.name;
      const uploadTaskFile = storage.ref(`/items/${fileName}`).put(item.file);
      uploadTaskFile.on(
        "state_changed",
        (snapshot) => {
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Upload is " + progress + "% done");
        },
        (error) => {
          console.log(error);
        },
        () => {
          uploadTaskFile.snapshot.ref.getDownloadURL().then((url) => {
            setHouseData((prev) => {
              return { ...prev, [item.label]: url };
            });
          });
        }
      );
    });
  };

  const handleUpload = (e) => {
    e.preventDefault();
    upload([
      { file: files, label: "files" },
      { file: video, label: "video" },
    ]);
    createHouse(housedata);
  };

    return (      
    <div style={{marginTop:"40px"}}>
      <h1 className="addProductTitle">New Movie</h1>
      <form className="addProductForm">
        <div className="addProductItem">
          <label>Image</label>
          <input
            type="file"
            id="img"
            name="files"
            multiple
            onChange={handleChangeForImg}
          />
        </div>
        <div className="addProductItem">
          <label>House Name</label>
          <input
            type="text"
            placeholder="Villa"
            name="housename"
            onChange={handleChange}
          />
        </div>
        <div className="addProductItem">
          <label>Description</label>
          <input
            type="text"
            placeholder="description"
            name="desc"
            onChange={handleChange}
          />
        </div>
        <div className="addProductItem">
          <label>Fee per Month</label>
          <input
            type="number"
            placeholder="Fee"
            name="fee"
            onChange={handleChange}
          />
        </div>
        <div className="addProductItem">
          <label>Size</label>
          <input
            type="text"
            placeholder="House Size"
            name="size"
            onChange={handleChange}
          />
        </div>
        <div className="addProductItem">
          <label>Number of Beds</label>
          <input
            type="text"
            placeholder="beds"
            name="beds"
            onChange={handleChange}
          />
        </div>
        <div className="addProductItem">
          <label>propertytype</label>
          <input
            type="text"
            placeholder="type"
            name="propertytype"
            onChange={handleChange}
          />
        </div>
        <div className="addProductItem">
          <label>Available?</label>
          <select name="available" id="available" onChange={handleChange}>
            <option value="false">No</option>
            <option value="true">Yes</option>
          </select>
        </div>
        <div className="addProductItem">
          <label>Video</label>
          <input
            type="file"
            name="video"
            onChange={(e) => setVideo(e.target.files[0])}
          />
        </div>
          <button className="addProductButton" onClick={handleUpload}>
            Upload
          </button>
      </form>
    </div>
    )
}

export default RegisterHouses
