import Navigator from "./components/Navigator";
import { createMedia } from '@artsy/fresnel'
import PropTypes from 'prop-types'
import React, { useState,useEffect } from 'react'
import { Link, useHistory,BrowserRouter as Router, Route } from 'react-router-dom'
import axios from 'axios'
import {
  Button,
  Container,
  Icon,
  Menu,
  Segment,
  Sidebar,
  Grid,
  List,Header, Dropdown} from 'semantic-ui-react'
import Home from "./components/pages/Home";
import About from './components/pages/About'
import Help from './components/pages/Help'
import FileUp from './components/screen/FileUp'
import HouseDetails from './components/screen/HouseDetails'
import HousesInTheSameDistrict from './components/screen/HousesInTheSameDistrict'
import Login from './components/screen/Login'
import Register from './components/screen/Register'
import SwiperSlider from './components/screen/SwiperSlider'
import PrivateRoute from './components/routing/PrivateRoute'
import LandlordRegister from './components/screen/LandlordRegister'
import LandlordLogin from './components/screen/LandlordLogin'
import LandlordProfile from './components/screen/LandlordProfile'
const { MediaContextProvider, Media } = createMedia({
  breakpoints: {
    mobile: 0,
    tablet: 768,
    computer: 1024,
  },
})

const DesktopContainer=({children})=>{
  const history = useHistory()
  const [fixed,toggleFixedBar] = useState(false)
  const showNav =()=>{
    toggleFixedBar(true)
  }
  const hideNav =()=>{
    toggleFixedBar(false)
  }

  const [activeItem,setActiveItem] = useState({
    name:'home'
  })

  const handleItemClick =(e,{name})=>{
    setActiveItem(name)
  }


   return (
     <Media greaterThan='mobile'>

           <Navigator/>
       {children}
     </Media>
   )
 }


DesktopContainer.propTypes = {
 children: PropTypes.node,
}

const MobileContainer =({children})=>{
  const [sidebarOpened, setOpenedSideBar] = useState(false)

/* thsi is a handler for sidebar toggler */
  const handleSidebarHide = () => {
    setOpenedSideBar(false)
  }

  const handleToggle = () =>{
    setOpenedSideBar(true)
  }


    return (
      <Media as={Sidebar.Pushable} at='mobile'>
        <Sidebar.Pushable>
          <Sidebar
            as={Menu}
            animation='overlay'
            inverted
            onHide={handleSidebarHide}
            vertical
            visible={sidebarOpened}
          >
            <Menu.Item as='a' active>
              Home
            </Menu.Item>
            <Menu.Item as='a'>Work</Menu.Item>
            <Menu.Item as='a'>Company</Menu.Item>
            <Menu.Item as='a'>Careers</Menu.Item>
            <Menu.Item as='a'>Log in</Menu.Item>
            <Menu.Item as='a'>Sign Up</Menu.Item>
          </Sidebar>

          <Sidebar.Pusher dimmed={sidebarOpened}>
            <Segment
              textAlign='center'
              style={{ minHeight: 350, padding: '1em 0em' }}
              vertical
            >
              <Container>
                <Menu pointing secondary size='large'>
                  <Menu.Item onClick={handleToggle}>
                    <Icon name='sidebar' />
                  </Menu.Item>
                  <Menu.Item position='right'>
                    <Button as='a' >
                      Log in
                    </Button>
                    <Button as='a'style={{ marginLeft: '0.5em' }}>
                      Sign Up
                    </Button>
                  </Menu.Item>
                </Menu>
              </Container>
              <Home/>
            </Segment>

            {children}
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </Media>
    )
  }


MobileContainer.propTypes = {
  children: PropTypes.node,
}
const ResponsiveContainer = ({ children }) => (
  /* Heads up!
   * For large applications it may not be best option to put all page into these containers at
   * they will be rendered twice for SSR.
   */
  <MediaContextProvider>
    <DesktopContainer>{children}</DesktopContainer>
    <MobileContainer>{children}</MobileContainer>
  </MediaContextProvider>
)

ResponsiveContainer.propTypes = {
  children: PropTypes.node,
}

function App() {
   const history = useHistory()
   const [activeItem,setActiveItem] = useState({
     name:'home'
   })

   const handleItemClick =(e,{name})=>{
     setActiveItem(name)
   }
  return (
    <div>
      <Router>
      <Menu 
            size='large'
            color='teal' 
            pointing 
            style={{height:'70px',marginBottom:"50px!"}}
           >

            <Menu.Item
              active={activeItem ==='home'}
              onClick={handleItemClick}
              as={Link}
              to={'/'}
            >
              KirayBet.com
            </Menu.Item>
            <Menu.Item
              color='red'
              active={activeItem === 'tenantscreen'}
              onClick={handleItemClick}
              as={Link}
              to={'/tenantscreen'}
            >
              Tenant Screen
            </Menu.Item>
            <Menu.Item

              active={activeItem === 'landlordscreen'}
              onClick={handleItemClick}
              as={Link}
              to={'/landordscreen'}
            >
            LandLord Screen
            </Menu.Item>
            <Menu.Item
              active={activeItem === 'listpropterty'}
              onClick={handleItemClick}
              as={Link}
              to={'/listpropterty'}
            >
            List Property
            </Menu.Item>
            <Menu.Item

              active={activeItem === 'managelisting'}
              onClick={handleItemClick}
              as={Link}
              to={'/managelisting'}
            >
            Manage Listing
            </Menu.Item>
            <Menu.Menu position='right'>
            <Menu.Item >
            <Dropdown
              text='LogIn'
              floating
              labeled
              button
              icon={null}
              style={{ marginRight: '0.5em' }}
            >
                  <Dropdown.Menu >
                    <Dropdown.Item 
                    // onClick={history.push('/login')}
                    as={Link}
                    to={'/landlordLogin'}
                    >LandLords Login</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item
                    // onClick={history.push('/login')}
                    as={Link}
                    to={'/login'}
                    >Tenants Login</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>
              <span>
                <pre>
                  |
                </pre>
              </span>
              <Dropdown
                text='SignUp'
                direction='left'
                floating
                labeled
                button
                icon={null}
                style={{ marginLeft: '0.5em' }}
              >
                  <Dropdown.Menu >
                  <Dropdown.Item
                  as={Link}
                  to={'/landlordRegister'}
                  >LandLords SignUp</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item
                    as={Link}
                    to={'/register'}
                    >Tenants SignUp</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>


            </Menu.Item>
            <Menu.Item>
              profile
            </Menu.Item>
            </Menu.Menu>
           </Menu>

           <Route exact path="/"><Home/></Route>
                <Route path="/about"><About/></Route>
                <Route path="/help"><Help/></Route>
                <Route path="/login"><Login/></Route>
                <Route path="/register"><Register/></Route>
                <Route path="/landlordLogin"><LandlordLogin/></Route>
                <Route path="/landlordRegister"><LandlordRegister/></Route>
                <Route path="/managelisting"><LandlordProfile/></Route>
                <PrivateRoute path="/listpropterty"><FileUp/></PrivateRoute>
                <Route path="/tenantscreen"><SwiperSlider/></Route>
                <Route path="/houseDetails/:productId"><HouseDetails/></Route>
                <Route path="/houseDistrict/:district"><HousesInTheSameDistrict/></Route>
      </Router>
      <ResponsiveContainer/> 
      <Segment inverted vertical style={{ padding: '5em 0em' }}>
        <Container>
          <Grid divided inverted stackable>
            <Grid.Row>
              <Grid.Column width={3}>
                <Header inverted as='h4' content='About' />
                <List link inverted>
                  <List.Item as='a'>Sitemap</List.Item>
                  <List.Item as='a'>Contact Us</List.Item>
                  <List.Item as='a'>Religious Ceremonies</List.Item>
                  <List.Item as='a'>Gazebo Plans</List.Item>
                </List>
              </Grid.Column>
              <Grid.Column width={3}>
                <Header inverted as='h4' content='Services' />
                <List link inverted>
                  <List.Item as='a'>Banana Pre-Order</List.Item>
                  <List.Item as='a'>DNA FAQ</List.Item>
                  <List.Item as='a'>How To Access</List.Item>
                  <List.Item as='a'>Favorite X-Men</List.Item>
                </List>
              </Grid.Column>
              <Grid.Column width={7}>
                <Header as='h4' inverted>
                  Footer Header
                </Header>
                <p>
                  Extra space for a call to action inside the footer that could help re-engage users.
                </p>
              </Grid.Column>
            </Grid.Row>
          </Grid>
        </Container>
      </Segment>
    </div>



  );
}

export default App;
