import React, { useEffect, useState, createRef } from 'react'
import axios from 'axios'

import SwiperCore, { Navigation, Pagination, Scrollbar } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.css';
import { Breadcrumb, Image, Ref, Segment, Container, TextArea, Checkbox, Divider, Header, Form, Button, Message, Grid, Modal, Icon, Sticky } from 'semantic-ui-react'
import { useParams, Link } from 'react-router-dom';
import RelatedHouses from './RelatedHouses';

SwiperCore.use([Navigation, Pagination, Scrollbar]);
const HouseDetails = ({ match }) => {
  const [open, setOpen] = useState(false)
  let params = useParams()

  const contextRef = createRef()
  const [multipleFiles, setMultipleFiles] = useState([])
  const [tenantId, setTenantId] = useState("")
  const [houseId, setHouseId] = useState("")
  const [tenantPhone, setTenantPhone] = useState(0)
  const [tenantEmail, setTenantEmail] = useState("")
  const productId = params.productId
  const apiUrl = `http://localhost:5000/api/getFilesById/${productId}`;

  const requestHouse = async (userData) => {
    const config = {
      header: {
        "Content-Type": "application/json",
      },
    };
    try {
      const res = await axios.post("http://localhost:5000/api/requestHouses", userData, config);
      console.log(res)
    } catch (err) {
      console.log(err)
    }
  };
  const handleRequest = async () => {
    const formData = new FormData();
    formData.append('tenantId', tenantId);
    formData.append('houseId', houseId);
    formData.append('tenantPhone', tenantPhone);
    formData.append('tenantEmail', tenantEmail);
    await requestHouse(formData);

  }
  const getMultipleFilesList = async () => {
    try {
      const { data } = await axios.get(apiUrl);
      setMultipleFiles(data);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getMultipleFilesList()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [productId])
  return (
    <div >
      {multipleFiles.map((element, index) =>
        <Ref innerRef={contextRef}>
          <Container style={{ marginTop: "80px" }}>
            <Grid textAlign='justified' style={{ marginBottom: "1px" }}>
              <Grid.Column width={11} verticalAlign='middle'>
                <Breadcrumb style={{ color: "teal" }}>
                  <Breadcrumb.Section link >Home</Breadcrumb.Section>
                  <Breadcrumb.Divider icon='right chevron' />
                  <Breadcrumb.Section as={Link} to={`/houseDistrict/${element.district}`}>{element.district}</Breadcrumb.Section>
                  <Breadcrumb.Divider icon='right chevron' />
                  <Breadcrumb.Section as={Link}>{element.sefer}</Breadcrumb.Section>
                  <Breadcrumb.Divider icon='right chevron' />
                  <Breadcrumb.Section active>{element.housename}</Breadcrumb.Section>
                </Breadcrumb>
              </Grid.Column>
              <Grid.Column width={5} textAlign='right'>
                <Modal
                  closeIcon
                  centered
                  open={open}
                  onClose={() => setOpen(false)}
                  onOpen={() => setOpen(true)}
                  trigger={<Button color="teal">
                    Request House

                  </Button>}
                  style={{ maxWidth: "400px", textAlign: "center" }}
                >
                  <Modal.Content>
                    <Form size='large' >
                      <Header as='h2' color='teal' textAlign='center'>
                        Request this house
                      </Header>
                      <Divider hidden />
                      {element.sefer, element.housename}
                      <Form.Field
                        required
                        id='form-input-control-last-name'
                        control={Input}
                        name='houseId'
                        type='text'
                        label='Tenant ID'
                        placeholder='Id'
                        onChange={()=>setHouseId(e.target.value)}
                      />
                      <Form.Field
                        required
                        id='form-input-control-last-name'
                        control={Input}
                        name='tenantId'
                        type='text'
                        label='Tenant Id'
                        placeholder='tenant Id'
                        onChange={()=>setTenantId(e.target.value)}
                      />
                      <Form.Field
                        required
                        id='form-input-control-last-name'
                        control={Input}
                        name='tenantPhone'
                        type='Number'
                        label='Phone Number'
                        onChange={()=>setTenantPhone(e.target.value)}
                      />
                      <Form.Field
                        required
                        id='form-input-control-last-name'
                        control={Input}
                        name='tenantEmail'
                        type='text'
                        label='Tenant Email'
                        placeholder='email'
                        onChange={()=>setTenantEmail(e.target.value)}
                      />
                      <Divider hidden />
                      <Button color="twitter" onClick={handleRequest}>Request House</Button>
                      <Divider hidden />
                      <Checkbox label={<label>In sending this message, you agree to KirayBet.com's Privacy Policy and Terms</label>} />


                    </Form>
                  </Modal.Content>

                </Modal>

                <Button >
                  <Icon name='phone' />0941454140
                </Button>
              </Grid.Column>
            </Grid>

            <Swiper
              slidesPerView={2}
              navigation
              pagination={{ clickable: true }}
              scrollbar={{ draggable: true }}
            >
              {element.files.map((url, index) =>
                <div className="col-6">
                  <SwiperSlide key={index} style={{ maxHeight: "70vh" }}>
                    <Image src={url} wrapped alt="img" style={{ objectFit: "contain" }} />
                  </SwiperSlide>
                </div>
              )}
            </Swiper>
            <Divider hidden />
            <Grid>
              <Grid.Column width={5}>
                {/* <Sticky active pushing context={contextRef}> */}

                <Segment stacked style={{ backgroundColor: "lightGray" }}>
                  <Form size='large'>
                    <Header as='h2' color='teal' textAlign='center'>
                      Request this house
                    </Header>
                    <Form.Input icon='user' iconPosition='left' type="email"
                      required
                      id="email"
                      placeholder="Email address"
                    />
                    <Form.Input

                      icon='lock'
                      iconPosition='left'
                      tabIndex={2}
                    />
                    <Form.Input
                      icon='lock'
                      iconPosition='left'
                      tabIndex={2}
                    />
                    <Form.Input
                      tabIndex={2}
                    />

                    <Button >Request House</Button>
                    <Message>
                      our privacy is important to us. In sending this message, you agree to KirayBet.com's Privacy Policy and Terms
                    </Message>
                  </Form>
                </Segment>
                {/* </Sticky> */}
              </Grid.Column>
              <Grid.Column width={11}>
                <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
              </Grid.Column>

            </Grid>



            <Divider hidden />

            <Header>People who viewed this House also viewed</Header>
            <RelatedHouses sefer={element.sefer} />

          </Container>
        </Ref>
      )}

    </div>
  )
}

export default HouseDetails
