import React, { useState, useEffect } from 'react'
import axios from 'axios'
import SwiperCore, { Navigation, Pagination, Scrollbar } from 'swiper';

import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.css';

import { Grid, Image, Card, Button, Icon, GridColumn, CardContent, CardDescription, Container } from 'semantic-ui-react'
import { Link } from 'react-router-dom';

import {StyledButton} from '../StyledComponent'
import HousesLocation from '../maps/HousesLocation';


SwiperCore.use([Navigation, Pagination, Scrollbar]);
const SwiperSlider = () => {

  const [multipleFiles, setMultipleFiles] = useState([]);
  const getMultipleFiles = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/getMultipleFiles');
      return data;
    } catch (error) {
      throw error;
    }
  }
  const getMultipleFilesList = async () => {
    try {
      const fileslist = await getMultipleFiles();
      setMultipleFiles(fileslist);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getMultipleFilesList()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return (
    <div style={{
      margin: "100px 10px 10px 20px",

    }}>
      <Container fluid>

      <Grid columns='2' style={{padding:"0px",margin:"0px"}}>
        <GridColumn>
          <Grid columns={2}>
            {multipleFiles.map((element, index) =>

              <div key={element._id} >
                <Card
                  style={{
                    height:"400px",
                    width:"100vw",
                    margin:"10px -10px"

                  }}
                >
                  <Grid.Column>
                    <Swiper
                      slidesPerView={1}
                      navigation
                      pagination={{ clickable: true }}
                      scrollbar={{ draggable: true }}
                    >
                      {element.files.map((url, index) =>

                        <SwiperSlide key={index}
                        style={{height:"250px"}}
                        >
                          <Link to={`/houseDetails/${element._id}`}>
                            <Image src={url} alt="img" 
                            style={{

                              objectFit:"cover"
                            }}
                            />
                          </Link>
                        </SwiperSlide>

                      )}
                    </Swiper>
                  </Grid.Column>
                  <CardContent>
                    <Card.Header>Matthew</Card.Header>
                    <Card.Meta>
                      {element.housename}
                    </Card.Meta>
                    <Card.Description>
                      {element.description}
                      ,{
                        element.district
                      },
                      {element.sefer}

                    </Card.Description>
                    <CardDescription>
                      <Button basic color='green'>
                        request
                      </Button>
                      <Button basic color='green'>
                        details
                      </Button>
                    </CardDescription>
                  </CardContent>

                </Card>
              </div>

            )}

          </Grid>
        </GridColumn>
        <GridColumn>
          <HousesLocation/>
        </GridColumn>
      </Grid>
      </Container>

    </div>
  )
}

export default SwiperSlider
