const ContractForm = () => {
  let params = useParams()
  const houseId = params.id
  const [house, setHouse] = useState([])
  const [tenantemail, setTenantemail] = useState('')
  const [houseid, setHouseid] = useState('')
  const [feepermonth, setFeepermonth] = useState(0)
  const [tenantusername, setTenantusername] = useState('')
  const [landlordemail, setLandlordemail] = useState('')
  const [landlordusername, setLandlordusername] = useState('')
  const [tenantsignature, setTenantSignature] = useState('')
  const [landlordsignature, setLandlordSignature] = useState('')
  const [termsandcondition, setTermsandcondition] = useState('')
  const [leaseduration, setLeaseduration] = useState('')
  const [trimmedDataURL,setTrimmedDataURL] = useState('')
  const handletenantEmailChange = (e) => {
    setTenantemail(e.target.value)
  }
  const handleTenantNameChange = (e) => {
    setTenantusername(e.target.value)
  }
  const handleLandlordEmailChange = (e) => {
    setLandlordemail(e.target.value)
  }
  const handleLandlordNameChange = (e) => {
    setLandlordusername(e.target.value)
  }
  const getThatHouse = async () => {
    try {
      const { data } = await axios.get(`http://localhost:5000/admin/gethouserequestbyid/${houseId}`)
      setHouse(data)
    } catch (error) {
      console.log(error)
    }
  }
  console.log(JSON.stringify(trimmedDataURL))
  const SignContract = (contractdata) => {
    const config = {
      header: {
        "Content-Type": "application/json",
      }
    };
    try {
      const { data } = axios.post('http://localhost:5000/admin/savecontract', contractdata, config)
      console.log(data)
    } catch (error) {
      console.log(error)
    }
  }
  const handleMultipleFileSubmit = async () => {
    const formData = new FormData();
    formData.append('houseid',houseid)
    formData.append('tenantname', tenantusername);
    formData.append('tenantemail', tenantemail);
    formData.append('landlordname', landlordusername);
    formData.append('landlordemail', landlordemail);
    formData.append('termsandcondition', termsandcondition);
    formData.append('contractduration', leaseduration);
    formData.append('feepermonth', feepermonth);
    formData.append('tenantsignature', tenantsignature);
    formData.append('landlordsignature', landlordsignature);

    SignContract(formData)


  }

  useEffect(() => {
    getThatHouse()
  }, [])
  return (
    <div style={{ minHeight: "100vh" }}>

      <Grid style={{ marginTop: "5px" }} >
        {
          house.map((element,index)=>
          <Grid.Column width={16}>
          <Segment
            textAlign='bottom'
            style={{
              backgroundImage: "url(/des2.jpg)",
              height: "20vh"
            }}
          >
            <Image src='https://react.semantic-ui.com/images/wireframe/square-image.png' size='small' circular />
          </Segment>
          <Divider hidden />
          <Divider hidden />
          <Header as='h2' >Terms And Conditions</Header>
          <Divider />
          <Grid textAlign='left'>
            <Grid.Column width="8"><Form size='large' style={{ verticalAlign: "center" }}>
              <Header> Tenant Contract Fill Form</Header>
              <Form.Input fluid icon='user' iconPosition='left' type="text"
                required
                placeholder="your username"
                tabIndex={1}
                onChange={e=>setTenantusername(e.target.value)}
              />
              <Form.Input fluid icon='email' iconPosition='left' type="email"
                required
                id="email"
                placeholder="your email address"
                tabIndex={1}
                onChange={e=>setTenantemail(e.target.value)}
              />




            </Form>
              <Segment>
                {/* <TextPlaceHolder trimmedDataURL={trimmedDataURL} onChange={setTrimmedDataURL}/> */}
                Tenant
              </Segment>

              <Divider hidden />
            </Grid.Column>

            <Grid.Column width="8"><Form size='large' style={{ verticalAlign: "center" }}>

              <Header> Landlord Contract Fill Form</Header>
              <Form.Input fluid icon='user' iconPosition='left' type="text"
                required
                placeholder="your username"
                tabIndex={1}
                onChange={e=>setLandlordusername(e.target.value)}
              />
              <Form.Input fluid icon='email' iconPosition='left' type="email"
                required
                id="email"
                placeholder="your email address"
                tabIndex={1}
                onChange={e=>setLandlordemail(e.target.value)}
              />
            </Form>

              <Segment>
                {/* <TextPlaceHolder /> */}
                Landlord
              </Segment>
              <Divider hidden />

              <Button >I agree to terms and condition</Button>
            </Grid.Column>






          </Grid>
        </Grid.Column>

      )}




      </Grid>

    </div>




  )
}
export default ContractForm;
