import React, {useState } from 'react'
import { Link, useHistory } from 'react-router-dom'
import {Button, Divider, Dropdown, Menu} from 'semantic-ui-react'
import { createMedia } from '@artsy/fresnel'
import PropTypes from 'prop-types'
const { MediaContextProvider, Media } = createMedia({
  breakpoints: {
    mobile: 0,
    tablet: 768,
    computer: 1024,
  },
})
const NavBar = (props) => {
  /* this is a handler for a navbar link item */
  const history = useHistory()
   const [activeItem,setActiveItem] = useState({
     name:'home'
   })

   const handleItemClick =(e,{name})=>{
     setActiveItem(name)
   }

    return (
      <Media greaterThan='mobile'>
        <div >
            <Menu 
            fixed='top'
            size='large'
            color='teal' 
            pointing 
            borderless
            style={{height:'70px'}}
           >

            <Menu.Item
              active={activeItem ==='home'}
              onClick={handleItemClick}
              as={Link}
              to={'/'}
            >
              KirayBet.com
            </Menu.Item>
            <Menu.Item
              color='red'
              active={activeItem === 'about'}
              onClick={handleItemClick}
              as={Link}
              to={'/about'}
            >
              Tenant Screen
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            Rent Pay
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            Other Services
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            help
            </Menu.Item>
            <Menu.Menu position='right'>
            <Menu.Item >
            <Dropdown
              text='LogIn'
              floating
              labeled
              button
              icon={null}
              style={{ marginRight: '0.5em' }}
            >
                  <Dropdown.Menu >
                    <Dropdown.Item 
                    onClick={history.push('/login')}
                    as={Link}
                    to={'/login'}
                    >LandLords Login</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item
                    onClick={history.push('/login')}
                    as={Link}
                    to={'/login'}
                    >Tenants Login</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>
              <span>
                <pre>
                  |
                </pre>
              </span>
              <Dropdown
                text='SignUp'
                direction='left'
                floating
                labeled
                button
                icon={null}
                style={{ marginLeft: '0.5em' }}
              >
                  <Dropdown.Menu >
                  <Dropdown.Item
                  onClick={history.push('/register')}
                  as={Link}
                  to={'/register'}
                  >LandLords SignUp</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item
                    onClick={history.push('/register')}
                    as={Link}
                    to={'/register'}
                    >Tenants SignUp</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>


            </Menu.Item>
            </Menu.Menu>
           </Menu>

        </div>
        </Media>
    )
}

export default NavBar
