import React, { useEffect, useState } from 'react'
import axios from 'axios'

import SwiperCore, { Navigation, Pagination, Scrollbar } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.css';
import { Breadcrumb, Image, Card, Segment, Container, Divider, Form,Input } from 'semantic-ui-react'
import { useParams,Link } from 'react-router-dom';

SwiperCore.use([Navigation, Pagination, Scrollbar]);
const RelatedHouses = ({sefer}) => {
  const [houseData,setHouseData]= useState([])
  const apiUrl = `http://localhost:5000/api/getFilesBySefer/${sefer}`;
  const getMultipleFilesList = async () => {
    try {
      const { data } = await axios.get(apiUrl);
      setHouseData(data);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getMultipleFilesList()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [houseData])
    return (
        <div>

      {houseData.map((element, index) =>
        <Container style={{marginTop:"100px"}} >


          <Segment >
            <Breadcrumb >
              <Breadcrumb.Section link>Home</Breadcrumb.Section>
              <Breadcrumb.Divider icon='right chevron' />
              <Breadcrumb.Section as={Link} to={`/houseDistrict/${element.district}`}>{element.district}</Breadcrumb.Section>
              <Breadcrumb.Divider icon='right chevron' />
              <Breadcrumb.Section active>{element.sefer}</Breadcrumb.Section>
            </Breadcrumb>
            <Swiper
              slidesPerView={2}
              navigation
              pagination={{ clickable: true }}
              scrollbar={{ draggable: true }}
            >
              {element.files.map((url, index) =>
                <div className="col-6">
                  <SwiperSlide key={index} style={{height:"500px"}}>
                    <Image src={url} wrapped alt="img" />
                  </SwiperSlide>
                </div>
              )}
            </Swiper>
          </Segment>
          <Divider />




        </Container>
      )}
        </div>
    )
}

export default RelatedHouses
