import React, {useState } from 'react'
import { Link } from 'react-router-dom'
import {Button, Label, Menu} from 'semantic-ui-react'

const NavBar = (props) => {
  /* this is a handler for a navbar link item */
   const [activeItem,setActiveItem] = useState({
     name:'home'
   })

   const handleItemClick =(e,{name})=>{
     setActiveItem(name)
   }

    return (
        <div >
            <Menu 
            fixed='top'
            size='large'
            color='teal' 
            pointing 
           >

            <Menu.Item
              active={activeItem ==='home'}
              onClick={handleItemClick}
              as={Link}
              to={'/'}
            >
              KirayBet.com
            </Menu.Item>
            <Menu.Item
              color='red'
              active={activeItem === 'about'}
              onClick={handleItemClick}
              as={Link}
              to={'/about'}
            >
              about
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            help
            </Menu.Item>
            <Menu.Item position='right'>
                      <Button as='a'>
                        Log in
                      </Button>
                      <Button as='a' primary={props.fixed} style={{ marginLeft: '0.5em' }}>
                        Sign Up
                      </Button>
            </Menu.Item>
           </Menu>

        </div>
    )
}

export default NavBar
