import axios from 'axios'
import React, { useEffect, useState } from 'react'
import {
  Button,
  Container, Divider, Form, Grid,
  Header, Image, Input, List, Segment
} from 'semantic-ui-react'

const Home = () => {
  const [multipleFiles, setMultipleFiles] = useState([]);

  const [houseMatch,setHousematch] = useState('')
  const [houses,setHouses]= useState([])
  const [text, setText] = useState('')
  const [options, setOptions] = useState([]);
  const getMultipleFiles = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/getMultipleFiles');
      return data;
    } catch (error) {
      throw error;
    }
  }
  const getMultipleFilesList = async () => {
    try {
      const fileslist = await getMultipleFiles();
      setMultipleFiles(fileslist);
    } catch (error) {
      console.log(error);
    }
  }

  const searchHouse= async ()=>{
    try {
      const searchedHouse = await axios.get(`http://localhost:5000/api/autoCompleteSearch?term=${text}`)
      setHouses(searchedHouse)
      console.log(searchedHouse)
    } catch (error) {


      console.log(error.message)
    }
  }
  useEffect(() => {
    const pokemon = [];
    const promises = new Array(20)
      .fill()
      .map((v, i) => fetch(`https://pokeapi.co/api/v2/pokemon-form/${i + 1}`));
    Promise.all(promises).then(pokemonArr => {
      return pokemonArr.map(value =>
        value
          .json()
          .then(({ name, sprites: { front_default: sprite } }) =>
            pokemon.push({ name, sprite })
          )
      );
    });
    setOptions(pokemon);
  }, []);
  useEffect(() => {

    getMultipleFilesList()
    searchHouse()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  return (
    <div>
      <Segment
        textAlign='center'
        style={{
          minHeight: 640,
          backgroundImage: "url(/des2.jpg)",
          height: "80vh"
        }}
      >
        <Header
          as='h1'
          content='Need a house near your workplace'
          style={{
            fontSize: '4em',
            fontFamily: "NunitoSans, Helvetica, sans-serif",
            fontWeight: 'bold',
            marginBottom: 0,
            marginTop: '3em',
            color: "white"
          }}
        />
        <Grid textAlign='center'>
          <Grid.Column style={{ maxWidth: 600 }} >
            <Segment style={{ borderColor: "#20c1c9", borderWidth: "3px", padding: "0px" }}>
              <Form size='large'>
                <Input
                  size='big' fluid type='search' placeholder='Search...'
                  action={{
                    content: 'Search',
                    style: { backgroundColor: "#20c1c9", width: "55px", padding: "7px" }

                  }}
                  onChange={(e)=>setText(e.target.value)}
                /* defaultValue={this.state.value}
                onChange={this.handleInputChange} 
               */
                />
              </Form>
            {/*   {
  houses.map((house)=><div>{house.housename}</div>)
} */}
            </Segment>


          </Grid.Column>
        </Grid>
      </Segment>
      <Divider horizontal>
        FOR RENTERS
      </Divider>
      {
        options.map(option=>{
          <div>
            {option.name}
          </div>
        })
      }
      <Segment style={{ padding: '8em 0em' }} vertical>
        <Grid container stackable verticalAlign='middle'>
          <Grid.Row>
            <Grid.Column width={8}>
              <Header as='h3' style={{ fontSize: '2em' }}>
                Let our search guide you
              </Header>
              <p style={{ fontSize: '1.33em' }}>
                Search thousands of up-to-date property listings on our easy-to-use website. Narrow down your options by choosing what's most important to you, such as number of bedrooms and bathrooms, price range, location, pet policy and more.
                Parents can also search for rentals that fall within a particular school district.
              </p>
              <Header as='h1' style={{ fontSize: '2em' }}>
                Simple and streamlined rental management all under a single roof
              </Header>
              <Header as='h4'>
                List your rental property
              </Header>
              <p style={{ fontSize: '1.33em' }}>

                Post your rental property in minutes to reach millions of potential renters.
              </p>
            </Grid.Column>
            <Grid.Column floated='right' width={6}>
              <Image bordered rounded size='large' src='/' />
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column textAlign='center'>
              <Button size='huge'>Check Them Out</Button>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Segment>

      <Segment style={{ padding: '0em' }} vertical>
        <Grid celled='internally' columns='equal' stackable>
          <Grid.Row textAlign='center'>
            <Grid.Column style={{ paddingBottom: '5em', paddingTop: '5em' }}>
              <Header as='h3' style={{ fontSize: '2em' }}>
                "What a Company"
              </Header>
              <p style={{ fontSize: '1.33em' }}>That is what they all say about us</p>
            </Grid.Column>
            <Grid.Column style={{ paddingBottom: '5em', paddingTop: '5em' }}>
              <Header as='h3' style={{ fontSize: '2em' }}>
                "I shouldn't have gone with their competitor."
              </Header>
              <p style={{ fontSize: '1.33em' }}>
                <Image avatar src='' />
                <b>Nan</b> Chief Fun Officer Acme Toys
              </p>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Segment>

      <Segment style={{ padding: '8em 0em' }} vertical>
        <Container text>
          <Header as='h3' style={{ fontSize: '2em' }}>
            Breaking The Grid, Grabs Your Attention
          </Header>
          <p style={{ fontSize: '1.33em' }}>
            Instead of focusing on content creation and hard work, we have learned how to master the
            art of doing nothing by providing massive amounts of whitespace and generic content that
            can seem massive, monolithic and worth your attention.
          </p>
          <Button as='a' size='large'>
            Read More
          </Button>

          <Divider
            as='h4'
            className='header'
            horizontal
            style={{ margin: '3em 0em', textTransform: 'uppercase' }}
          >
            <a href='#'>Case Studies</a>
          </Divider>

          <Header as='h3' style={{ fontSize: '2em' }}>
            Search By District and Base(Sefer)
          </Header>
          <Grid columns={2}>
            {multipleFiles.map((element, index) =>
              <div key={element._id}>
                <List>
                  <List.Item
                    content={<a href='mailto:jack@semantic-ui.com'>{element.sefer}</a>}
                  />
                </List>
              </div>
            )}

          </Grid>

        </Container>
      </Segment>


    </div>

  )
}

export default Home
