import { createMedia } from '@artsy/fresnel'
import PropTypes from 'prop-types'
import React, { useState,useEffect } from 'react'
import axios from 'axios'
import {
  Button,
  Container,
  Grid,
  Header,
  Icon,
  Image,
  List,
  Menu,
  Form,
  Input,
  Segment,
  Sidebar,
  Visibility,Divider, Dropdown
} from 'semantic-ui-react'
import { Link, useHistory } from 'react-router-dom'

const { MediaContextProvider, Media } = createMedia({
  breakpoints: {
    mobile: 0,
    tablet: 768,
    computer: 1024,
  },
})

/* Heads up!
 * HomepageHeading uses inline styling, however it's not the best practice. Use CSS or styled
 * components for such things.
 */
const HomepageHeading = ({ mobile }) => (
  <Container >
      <Header
      as='h1'
      content='Need a house near your workplace'
      style={{
        fontSize: mobile ? '2em' : '4em',
        fontWeight: 'bold',
        marginBottom: 0,
        marginTop: mobile ? '1.5em' : '3em',
      }}
    />
    <Header
      as='h2'
      content='We have an affordable house with 100% warranty'
      style={{
        fontSize: mobile ? '1.5em' : '1.7em',
        fontWeight: 'normal',
        marginTop: mobile ? '0.5em' : '1.5em',
      }}
    />
    <Grid textAlign='center'>
    <Grid.Column style={{ maxWidth: 600 }} >
      <div style={{borderColor:"teal"}}>
      <Form size='large' success >
      <Input 
      size='big' fluid type='search' action='Search' placeholder='Search...'
      /* action={{
        icon: "play",
        onClick: () => this.handleClick()
      }}
      defaultValue={this.state.value}
      onChange={this.handleInputChange} */
      style={{borderColor:'teal'}}
      />
      </Form>
      </div>


    </Grid.Column>
  </Grid>


  </Container>
)

HomepageHeading.propTypes = {
  mobile: PropTypes.bool,
}

/* Heads up!
 * Neither Semantic UI nor Semantic UI React offer a responsive navbar, however, it can be implemented easily.
 * It can be more complicated, but you can create really flexible markup.
 */
