   const [fixed,toggleFixedBar] = useState(false)
   const showNav =()=>{
     toggleFixedBar(true)
   }
   const hideNav =()=>{
     toggleFixedBar(false)
   }
   const history = useHistory()
   const [activeItem,setActiveItem] = useState({
     name:'home'
   })

   const handleItemClick =(e,{name})=>{
     setActiveItem(name)
   }

    return (
      <Media greaterThan='mobile'>
        <Visibility
          once={false}
          onBottomPassed={showNav}
          onBottomPassedReverse={hideNav}
        >
          <Segment
            textAlign='center'
            style={{ 
              minHeight: 640,
              backgroundImage: "url(/des2.jpg)",
              height:"80vh"
            }} 
          > 
          {/* <Menu 
            fixed='top'
            size='large'
            color='teal' 
            pointing 
            borderless
            style={{height:'70px'}}
           >

            <Menu.Item
              active={activeItem ==='home'}
              onClick={handleItemClick}
              as={Link}
              to={'/'}
            >
              KirayBet.com
            </Menu.Item>
            <Menu.Item
              color='red'
              active={activeItem === 'about'}
              onClick={handleItemClick}
              as={Link}
              to={'/about'}
            >
              Tenant Screen
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            Rent Pay
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            Other Services
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            help
            </Menu.Item>
            <Menu.Menu position='right'>
            <Menu.Item >
            <Dropdown
              text='LogIn'
              floating
              labeled
              button
              icon={null}
              style={{ marginRight: '0.5em' }}
            >
                  <Dropdown.Menu >
                    <Dropdown.Item 
                    onClick={history.push('/login')}
                    as={Link}
                    to={'/login'}
                    >LandLords Login</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item
                    onClick={history.push('/login')}
                    as={Link}
                    to={'/login'}
                    >Tenants Login</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>
              <span>
                <pre>
                  |
                </pre>
              </span>
              <Dropdown
                text='SignUp'
                direction='left'
                floating
                labeled
                button
                icon={null}
                style={{ marginLeft: '0.5em' }}
              >
                  <Dropdown.Menu >
                  <Dropdown.Item
                  onClick={history.push('/register')}
                  as={Link}
                  to={'/register'}
                  >LandLords SignUp</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item
                    onClick={history.push('/register')}
                    as={Link}
                    to={'/register'}
                    >Tenants SignUp</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>


            </Menu.Item>
            </Menu.Menu>
           </Menu> */}
            <HomepageHeading />
          </Segment>
        </Visibility>

        {children}
      </Media>
    )
  }


DesktopContainer.propTypes = {
  children: PropTypes.node,
}

const MobileContainer =({children})=>{
  const [sidebarOpened, setOpenedSideBar] = useState(false)

/* thsi is a handler for sidebar toggler */
  const handleSidebarHide = () => {
    setOpenedSideBar(false)
  }

  const handleToggle = () =>{
    setOpenedSideBar(true)
  }


    return (
      <Media as={Sidebar.Pushable} at='mobile'>
        <Sidebar.Pushable>
          <Sidebar
            as={Menu}
            animation='overlay'
            inverted
            onHide={handleSidebarHide}
            vertical
            visible={sidebarOpened}
          >
            <Menu.Item as='a' active>
              Home
            </Menu.Item>
            <Menu.Item as='a'>Work</Menu.Item>
            <Menu.Item as='a'>Company</Menu.Item>
            <Menu.Item as='a'>Careers</Menu.Item>
            <Menu.Item as='a'>Log in</Menu.Item>
            <Menu.Item as='a'>Sign Up</Menu.Item>
          </Sidebar>

          <Sidebar.Pusher dimmed={sidebarOpened}>
            <Segment
              inverted
              textAlign='center'
              style={{ minHeight: 350, padding: '1em 0em' }}
              vertical
            >
              <Container>
                <Menu inverted pointing secondary size='large'>
                  <Menu.Item onClick={handleToggle}>
                    <Icon name='sidebar' />
                  </Menu.Item>
                  <Menu.Item position='right'>
                    <Button as='a' inverted>
                      Log in
                    </Button>
                    <Button as='a' inverted style={{ marginLeft: '0.5em' }}>
                      Sign Up
                    </Button>
                  </Menu.Item>
                </Menu>
              </Container>
              <HomepageHeading mobile />
            </Segment>

            {children}
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </Media>
    )
  }


MobileContainer.propTypes = {
  children: PropTypes.node,
}

const ResponsiveContainer = ({ children }) => (
  /* Heads up!
   * For large applications it may not be best option to put all page into these containers at
   * they will be rendered twice for SSR.
   */
  <MediaContextProvider>
    <DesktopContainer>{children}</DesktopContainer>
    <MobileContainer>{children}</MobileContainer>
  </MediaContextProvider>
)

ResponsiveContainer.propTypes = {
  children: PropTypes.node,
}

const Home = () => {
  const [multipleFiles, setMultipleFiles] = useState([]);
  const getMultipleFiles = async () => {
    try{
        const {data} = await axios.get('http://localhost:5000/api/getMultipleFiles');
        return data;
    }catch(error){
        throw error;
    }
}
  const getMultipleFilesList = async () => {
    try {
        const fileslist = await getMultipleFiles();
        setMultipleFiles(fileslist);
    } catch (error) {
      console.log(error);
    }
  }
    useEffect(() => {
      getMultipleFilesList()
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);
  return(
  <ResponsiveContainer>
    <Divider horizontal>
      FOR RENTERS
    </Divider>
    <Segment style={{ padding: '8em 0em' }} vertical>
      <Grid container stackable verticalAlign='middle'>
        <Grid.Row>
          <Grid.Column width={8}>
            <Header as='h3' style={{ fontSize: '2em' }}>
            Let our search guide you
            </Header>
            <p style={{ fontSize: '1.33em' }}>
            Search thousands of up-to-date property listings on our easy-to-use website. Narrow down your options by choosing what's most important to you, such as number of bedrooms and bathrooms, price range, location, pet policy and more.
            Parents can also search for rentals that fall within a particular school district.
            </p>
            <Header as='h1' style={{ fontSize: '2em' }}>
            Simple and streamlined rental management all under a single roof
            </Header>
            <Header as='h4'>
            List your rental property
            </Header>
            <p style={{ fontSize: '1.33em' }}>

Post your rental property in minutes to reach millions of potential renters.
            </p>
          </Grid.Column>
          <Grid.Column floated='right' width={6}>
            <Image bordered rounded size='large' src='/' />
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column textAlign='center'>
            <Button size='huge'>Check Them Out</Button>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </Segment>

    <Segment style={{ padding: '0em' }} vertical>
      <Grid celled='internally' columns='equal' stackable>
        <Grid.Row textAlign='center'>
          <Grid.Column style={{ paddingBottom: '5em', paddingTop: '5em' }}>
            <Header as='h3' style={{ fontSize: '2em' }}>
              "What a Company"
            </Header>
            <p style={{ fontSize: '1.33em' }}>That is what they all say about us</p>
          </Grid.Column>
          <Grid.Column style={{ paddingBottom: '5em', paddingTop: '5em' }}>
            <Header as='h3' style={{ fontSize: '2em' }}>
              "I shouldn't have gone with their competitor."
            </Header>
            <p style={{ fontSize: '1.33em' }}>
              <Image avatar src='' />
              <b>Nan</b> Chief Fun Officer Acme Toys
            </p>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    </Segment>

    <Segment style={{ padding: '8em 0em' }} vertical>
      <Container text>
        <Header as='h3' style={{ fontSize: '2em' }}>
          Breaking The Grid, Grabs Your Attention
        </Header>
        <p style={{ fontSize: '1.33em' }}>
          Instead of focusing on content creation and hard work, we have learned how to master the
          art of doing nothing by providing massive amounts of whitespace and generic content that
          can seem massive, monolithic and worth your attention.
        </p>
        <Button as='a' size='large'>
          Read More
        </Button>

        <Divider
          as='h4'
          className='header'
          horizontal
          style={{ margin: '3em 0em', textTransform: 'uppercase' }}
        >
          <a href='#'>Case Studies</a>
        </Divider>

        <Header as='h3' style={{ fontSize: '2em' }}>
        Search By District and Base(Sefer)
        </Header>
        <Grid columns={2}>
            {multipleFiles.map((element, index) =>
                <div key={element._id}>
                   <List>
                      <List.Item
                        content={<a href='mailto:jack@semantic-ui.com'>{element.sefer}</a>}
                      />
                  </List>
                </div>
              )}

            </Grid>

      </Container>
    </Segment>

    <Segment inverted vertical style={{ padding: '5em 0em' }}>
      <Container>
        <Grid divided inverted stackable>
          <Grid.Row>
            <Grid.Column width={3}>
              <Header inverted as='h4' content='About' />
              <List link inverted>
                <List.Item as='a'>Sitemap</List.Item>
                <List.Item as='a'>Contact Us</List.Item>
                <List.Item as='a'>Religious Ceremonies</List.Item>
                <List.Item as='a'>Gazebo Plans</List.Item>
              </List>
            </Grid.Column>
            <Grid.Column width={3}>
              <Header inverted as='h4' content='Services' />
              <List link inverted>
                <List.Item as='a'>Banana Pre-Order</List.Item>
                <List.Item as='a'>DNA FAQ</List.Item>
                <List.Item as='a'>How To Access</List.Item>
                <List.Item as='a'>Favorite X-Men</List.Item>
              </List>
            </Grid.Column>
            <Grid.Column width={7}>
              <Header as='h4' inverted>
                Footer Header
              </Header>
              <p>
                Extra space for a call to action inside the footer that could help re-engage users.
              </p>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Container>
    </Segment>
  </ResponsiveContainer>
  )
}

export default Home
