import React, {useState } from 'react'
import { Link } from 'react-router-dom'
import {Button, Label, Menu} from 'semantic-ui-react'
import { createMedia } from '@artsy/fresnel'
const { MediaContextProvider, Media } = createMedia({
  breakpoints: {
    mobile: 0,
    tablet: 768,
    computer: 1024,
  },
})
const DesktopContainer=({children})=>{
  const [fixed,toggleFixedBar] = useState(false)
  const showNav =()=>{
    toggleFixedBar(true)
  }
  const hideNav =()=>{
    toggleFixedBar(false)
  }

 return (
     <Media greaterThan='mobile'>
       <Visibility
         once={false}
         onBottomPassed={showNav}
         onBottomPassedReverse={hideNav}
       >
         <Segment
           textAlign='center'
           style={{ 
             minHeight: 700,
             backgroundImage: "url(/des2.jpg)",
             height:"80vh"
           }} 
         >
           <NavBar fixed={fixed}/>   
           <HomepageHeading />
         </Segment>
       </Visibility>

       {children}
     </Media>
   )
 }


DesktopContainer.propTypes = {
 children: PropTypes.node,
}
const NavBar = (props) => {
  /* this is a handler for a navbar link item */
   const [activeItem,setActiveItem] = useState({
     name:'home'
   })

   const handleItemClick =(e,{name})=>{
     setActiveItem(name)
   }

    return (
        <div >
            <Menu 
            fixed={props.fixed ? 'top' : null}
            //inverted={!fixed}
            pointing={props.fixed}
            secondary={props.fixed}
            size='large'

           >

            <Menu.Item
              active={activeItem ==='home'}
              onClick={handleItemClick}
              as={Link}
              to={'/'}
            >
              KirayBet.com
            </Menu.Item>
            <Menu.Item
              color='red'
              active={activeItem === 'about'}
              onClick={handleItemClick}
              as={Link}
              to={'/about'}
            >
              about
            </Menu.Item>
            <Menu.Item
              name='help'
              active={activeItem === 'help'}
              onClick={handleItemClick}
              as={Link}
              to={'/help'}
            >
            help
            </Menu.Item>
            <Menu.Item position='right'>
                      <Button as='a'>
                        Log in
                      </Button>
                      <Button as='a' primary={props.fixed} style={{ marginLeft: '0.5em' }}>
                        Sign Up
                      </Button>
            </Menu.Item>
           </Menu>

        </div>
    )
}

export default NavBar
