import React, { useEffect, useState } from 'react'
import axios from 'axios'

import SwiperCore, { Navigation, Pagination, Scrollbar } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.css';
import { Breadcrumb, Image, Card, Segment, Container } from 'semantic-ui-react'
import { useParams,Link } from 'react-router-dom';

SwiperCore.use([Navigation, Pagination, Scrollbar]);
const HousesInTheSameDistrict = ({ match }) => {
  let params = useParams()
  const [multipleFiles, setMultipleFiles] = useState([])
  const housesDistrict = params.district
  const apiUrl = `http://localhost:5000/api/getFilesByDistrict/${housesDistrict}`;


  const getMultipleFilesList = async () => {
    try {
      const { data } = await axios.get(apiUrl);
      console.log(data)
      setMultipleFiles(data);
      console.log(multipleFiles);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getMultipleFilesList()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [housesDistrict])
  return (
    <div >

<Container fluid >
          <Grid columns='2' style={{ padding: "0px", margin: "0px" }}>
            <GridColumn style={{ padding: "0px", margin: "0px" }}>
            <Form>
              <Form.Group widths='equal'>

                  <Form.Input
                    type='search' placeholder='Search...'
                    action={{
                      icon: 'search',


                    }}
                  /* defaultValue={this.state.value}
                  onChange={this.handleInputChange} 
                 */
                  />

              </Form.Group>
            </Form>
              <Grid columns={2} style={{ padding: "0px", margin: "0px", width: "70vw" }}>
                {multipleFiles.map((element, index) =>

                  <div key={element._id} >
                    <Card
                      style={{
                        height: "350px",
                        width: "308px",
                        margin: "5px 0px",
                      }}
                    >
                      <Grid.Column style={{ padding: "0px", margin: "0px" }}>
                        <Swiper
                          slidesPerView={1}
                          navigation
                          pagination={{ clickable: true }}
                          scrollbar={{ draggable: true }}
                        >
                          {element.files.map((url, index) =>

                            <SwiperSlide key={index}
                              style={{ height: "250px" }}
                            >
                              <Link to={`/houseDetails/${element._id}`}>
                                <Image src={url} alt="img"
                                  style={{

                                    objectFit: "cover"
                                  }}
                                />
                              </Link>
                            </SwiperSlide>

                          )}
                        </Swiper>
                      </Grid.Column>
                      <CardContent>
                        <Card.Header>Matthew</Card.Header>
                        <Card.Meta>
                          {element.housename}
                        </Card.Meta>
                        <Card.Description>

                          {
                            element.district
                          },
                          {element.sefer}
                        </Card.Description>
                      </CardContent>

                    </Card>
                  </div>

                )}

              </Grid>
            </GridColumn>
            <GridColumn>
              <Sticky context={contextRef} offset={60}>
                <HousesLocation />
              </Sticky>

            </GridColumn>
          </Grid>
        </Container>
    </div>
  )
}

export default HousesInTheSameDistrict
