import axios from 'axios'
import React,{useState} from 'react'
import { Form,Button,Input,Header,Checkbox,Divider } from 'semantic-ui-react'
const ContractForm = () => {
    const [recipient, setRecipient] = useState('')
    const sendEmail = async (e) => {
        e.preventDefault();

        const config = {
          header: {
            "Content-Type": "application/json",
          },
        };

        try {
          const { data } = await axios.post(
            "http://localhost:5000/api/sendContractForm",
            { recipient },
            config
          );

        } catch (error) {
        console.log(error)
        }
      };
    return (
        <div>
            <Form size='large' >
                      <Header as='h2' color='teal' textAlign='center'>
                        send a contract form
                      </Header>
                      <Divider hidden />

                      <Form.Field
                        required
                        id='form-input-control-last-name'
                        control={Input}
                        name='tenantEmail'
                        type='text'
                        placeholder='email of the tenant who requested the house'
                        label='Email'
                        value={recipient}
                        onChange={e=>setRecipient(e.target.value)}
                      />
                      <Divider hidden />
                      <Button color="twitter" onClick={sendEmail} >Send Email</Button>

                    </Form>
        </div>
    )
}

export default ContractForm
