import React, { useEffect, useState } from 'react'
import axios from 'axios'

import SwiperCore, { Navigation, Pagination, Scrollbar } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.css';
import { Breadcrumb, Image, Card, Segment, Container, Divider, Header, Form, Button, Message, Grid, GridColumn } from 'semantic-ui-react'
import { useParams, Link } from 'react-router-dom';
import RelatedHouses from './RelatedHouses';

SwiperCore.use([Navigation, Pagination, Scrollbar]);
const HouseDetails = ({ match }) => {
  let params = useParams()
  const [multipleFiles, setMultipleFiles] = useState([])
  const [houseData, setHouseData] = useState([])
  const productId = params.productId
  const apiUrl = `http://localhost:5000/api/getFilesById/${productId}`;
  var relatedHouses;

  const getMultipleFilesList = async () => {
    try {
      const { data } = await axios.get(apiUrl);
      setMultipleFiles(data);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    getMultipleFilesList()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [productId])
  return (
    <div >

      {multipleFiles.map((element, index) =>
        <Container style={{ marginTop: "100px" }}>
          <Grid>
            <Grid.Column width={11}>
              <Breadcrumb style={{ color: "teal" }}>
                <Breadcrumb.Section link >Home</Breadcrumb.Section>
                <Breadcrumb.Divider icon='right chevron' />
                <Breadcrumb.Section as={Link} to={`/houseDistrict/${element.district}`}>{element.district}</Breadcrumb.Section>
                <Breadcrumb.Divider icon='right chevron' />
                <Breadcrumb.Section as={Link}>{element.sefer}</Breadcrumb.Section>
                <Breadcrumb.Divider icon='right chevron' />
                <Breadcrumb.Section active>{element.housename}</Breadcrumb.Section>
              </Breadcrumb>
            </Grid.Column>
            <Grid.Column width={5} textAlign='right'>
              <Button >
                Request House
              </Button>
              <Button >
                Request House
              </Button>
            </Grid.Column>
          </Grid>
          <Divider hidden/>
          <Swiper
            slidesPerView={2}
            navigation
            pagination={{ clickable: true }}
            scrollbar={{ draggable: true }}
          >
            {element.files.map((url, index) =>
              <div className="col-6">
                <SwiperSlide key={index} style={{ maxHeight: "70vh" }}>
                  <Image src={url} wrapped alt="img" style={{ objectFit: "contain" }} />
                </SwiperSlide>
              </div>
            )}
          </Swiper>
          <Divider />
          <Grid>
            <Grid.Column width={6}>
              <Header as='h2' color='teal' textAlign='center'>
                Request this house
              </Header>
              <Form size='large'>
                <Segment stacked>
                  <Form.Input fluid icon='user' iconPosition='left' type="email"
                    required
                    id="email"
                    placeholder="Email address"
                  />
                  <Form.Input
                    fluid
                    icon='lock'
                    iconPosition='left'

                    tabIndex={2}
                  />

                  <Button >Request House</Button>
                </Segment>
              </Form>
              <Message>
                dfldjsfldjflsdjfl
              </Message>

            </Grid.Column>
            <Grid.Column width={10}>
              <Image src='https://react.semantic-ui.com/images/wireframe/paragraph.png' />
            </Grid.Column>

          </Grid>

          <Divider />

          <Header>People who viewed this House also viewed</Header>
          <RelatedHouses sefer={element.sefer} />



        </Container>
      )}

    </div>
  )
}

export default HouseDetails
