import React from 'react'
import { Header,Button} from 'semantic-ui-react';

const SendAppointment = () => {

    const publicVapidKey = 'BMffGk0gRxLPOSi-eOlXoR1ahY9Ce7uBY3010C06TeMoRYS_6n1A4ItVeOeNYutDlhPK27WW5UMrdyjBEj_-Pxo';

    //register the service worker, register our push api, sedn the notifation
    async function send(){
        //register service worker
        const register = await navigator.serviceWorker.register('/sw.js', {
            scope: '/'
        });

        //register push
        console.log('Registering push...')
        const subscription = await register.pushManager.subscribe({
            userVisibleOnly: true,
            applicationServerKey: urlBase64ToUint8Array(publicVapidKey)
        });

        //Send push notification
        await fetch("http://localhost:5000/api/subscribe", {
            method: "POST",
            body: JSON.stringify(subscription),
            headers: {
                "content-type": "application/json"
            }
        });
    }

    function urlBase64ToUint8Array(base64String) {
        const padding = "=".repeat((4 - base64String.length % 4) % 4);
        const base64 = (base64String + padding)
          .replace(/\-/g, "+")
          .replace(/_/g, "/");

        const rawData = window.atob(base64);
        const outputArray = new Uint8Array(rawData.length);

        for (let i = 0; i < rawData.length; ++i) {
          outputArray[i] = rawData.charCodeAt(i);
        }
        return outputArray;
    }
    return (
        <div style={{alignContent:"center",margin:"150px"}}>
            <h2>
                hello the landlord appointed you 
            </h2>
            <Button onClick={send}>
                send appointment
            </Button>
        </div>
    )
}

export default SendAppointment
