import Navigator from "./components/Navigator";
import { createMedia } from '@artsy/fresnel'
import PropTypes from 'prop-types'
import React, { useState,useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import axios from 'axios'
import {
  Button,
  Container,
  Icon,
  Menu,
  Segment,
  Sidebar,
  Visibility,
  Dropdown} from 'semantic-ui-react'
import Home from "./components/pages/Home";

const { MediaContextProvider, Media } = createMedia({
  breakpoints: {
    mobile: 0,
    tablet: 768,
    computer: 1024,
  },
})

const DesktopContainer=({children})=>{
  const history = useHistory()
  const [fixed,toggleFixedBar] = useState(false)
  const showNav =()=>{
    toggleFixedBar(true)
  }
  const hideNav =()=>{
    toggleFixedBar(false)
  }

  const [activeItem,setActiveItem] = useState({
    name:'home'
  })

  const handleItemClick =(e,{name})=>{
    setActiveItem(name)
  }


   return (
     <Media greaterThan='mobile'>
       <Visibility
         once={false}
         onBottomPassed={showNav}
         onBottomPassedReverse={hideNav}
       >
         <Segment
           textAlign='center'
           style={{ 
             minHeight: 640,
             backgroundImage: "url(/des2.jpg)",
             height:"80vh"
           }} 
         > 
         <Menu 
            fixed='top'
            size='large'
            color='teal' 
            pointing 
            borderless
            style={{height:'70px'}}
           >

            <Menu.Item
              active={activeItem ==='home'}
              onClick={handleItemClick}
              as={Link}
              to={'/'}
            >
              KirayBet.com
            </Menu.Item>
            <Menu.Item
              color='red'
              active={activeItem === 'tenantscreen'}
              onClick={handleItemClick}
              as={Link}
              to={'/tenantscreen'}
            >
              Tenant Screen
            </Menu.Item>
            <Menu.Item

              active={activeItem === 'landlordscreen'}
              onClick={handleItemClick}
              as={Link}
              to={'/landordscreen'}
            >
            LandLord Screen
            </Menu.Item>
            <Menu.Item
              active={activeItem === 'listpropterty'}
              onClick={handleItemClick}
              as={Link}
              to={'/listpropterty'}
            >
            List Property
            </Menu.Item>
            <Menu.Item

              active={activeItem === 'managelisting'}
              onClick={handleItemClick}
              as={Link}
              to={'/managelisting'}
            >
            Manage Listing
            </Menu.Item>
            <Menu.Menu position='right'>
            <Menu.Item >
            <Dropdown
              text='LogIn'
              floating
              labeled
              button
              icon={null}
              style={{ marginRight: '0.5em' }}
            >
                  <Dropdown.Menu >
                    <Dropdown.Item 

                    as={Link}
                    to={'/login'}
                    >LandLords Login</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item

                    as={Link}
                    to={'/login'}
                    >Tenants Login</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>
              <span>
                <pre>
                  |
                </pre>
              </span>
              <Dropdown
                text='SignUp'
                direction='left'
                floating
                labeled
                button
                icon={null}
                style={{ marginLeft: '0.5em' }}
              >
                  <Dropdown.Menu >
                  <Dropdown.Item

                  as={Link}
                  to={'/register'}
                  >LandLords SignUp</Dropdown.Item>
                    <Dropdown.Divider />
                    <Dropdown.Item

                    as={Link}
                    to={'/register'}
                    >Tenants SignUp</Dropdown.Item>
                  </Dropdown.Menu>
              </Dropdown>


            </Menu.Item>
            <Menu.Item>
              profile
            </Menu.Item>
            </Menu.Menu>
           </Menu>
           <Navigator/>
         </Segment>
       </Visibility>

       {children}
     </Media>
   )
 }


DesktopContainer.propTypes = {
 children: PropTypes.node,
}

const MobileContainer =({children})=>{
  const [sidebarOpened, setOpenedSideBar] = useState(false)

/* thsi is a handler for sidebar toggler */
  const handleSidebarHide = () => {
    setOpenedSideBar(false)
  }

  const handleToggle = () =>{
    setOpenedSideBar(true)
  }


    return (
      <Media as={Sidebar.Pushable} at='mobile'>
        <Sidebar.Pushable>
          <Sidebar
            as={Menu}
            animation='overlay'
            inverted
            onHide={handleSidebarHide}
            vertical
            visible={sidebarOpened}
          >
            <Menu.Item as='a' active>
              Home
            </Menu.Item>
            <Menu.Item as='a'>Work</Menu.Item>
            <Menu.Item as='a'>Company</Menu.Item>
            <Menu.Item as='a'>Careers</Menu.Item>
            <Menu.Item as='a'>Log in</Menu.Item>
            <Menu.Item as='a'>Sign Up</Menu.Item>
          </Sidebar>

          <Sidebar.Pusher dimmed={sidebarOpened}>
            <Segment
              inverted
              textAlign='center'
              style={{ minHeight: 350, padding: '1em 0em' }}
              vertical
            >
              <Container>
                <Menu inverted pointing secondary size='large'>
                  <Menu.Item onClick={handleToggle}>
                    <Icon name='sidebar' />
                  </Menu.Item>
                  <Menu.Item position='right'>
                    <Button as='a' inverted>
                      Log in
                    </Button>
                    <Button as='a' inverted style={{ marginLeft: '0.5em' }}>
                      Sign Up
                    </Button>
                  </Menu.Item>
                </Menu>
              </Container>
              <Home/>
            </Segment>

            {children}
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </Media>
    )
  }


MobileContainer.propTypes = {
  children: PropTypes.node,
}
const ResponsiveContainer = ({ children }) => (
  /* Heads up!
   * For large applications it may not be best option to put all page into these containers at
   * they will be rendered twice for SSR.
   */
  <MediaContextProvider>
    <DesktopContainer>{children}</DesktopContainer>
    <MobileContainer>{children}</MobileContainer>
  </MediaContextProvider>
)

ResponsiveContainer.propTypes = {
  children: PropTypes.node,
}

function App() {
  return (
    <ResponsiveContainer/>



  );
}

export default App;
